// The context of the sub library	} NSBTreoLibClientContextType;typedef NSBTreoLibClientContextType*		NSBTreoLibClientContextPtr;// The wSignature field of each NSBTreoLibClientContextType will be set to// this value for debugging#define sampleLibContextSignature		(0xFEED)/******************************************************************** * Private Macros ********************************************************************/#define sampleDefaultCornerDiameter		(0)#endif	// __SAMPLE_LIB_PRV_H__